    echo "Population: " . number_format($nigeria_states[$i]["Population"]) . "\n";
    echo "Mineral Resources: " . implode(", ", $nigeria_states[$i]["Minerals"]) . "\n";

    // If condition to check for states with Oil/Gas
    if (in_array("Oil/Gas", $nigeria_states[$i]["Minerals"])) {
        echo "Note: This state is a major oil-producing region.\n";
    }

    // If condition to check for states with population > 10 million
    if ($nigeria_states[$i]["Population"] > 10000000) {
        echo "Note: This state has a high population density (over 10 million).\n";
    }

